import React from "react";

const ProgressBar = ({progress}) => {
    return (
      <div className="outer-bar">
        <div className="inner-bar"
        style={{ width: `${progress}%`, backgroundColor: "green" }}
        >
          
        </div>
      </div>
    );
  }
  
  export default ProgressBar;

