import React from 'react';
import { render, fireEvent, screen } from '@testing-library/react';
import { expect } from 'chai';
import chai from 'chai';
import chaiHttp from 'chai-http';
import ListHeader from '../src/components/ListHeader';
import ListItem from '../src/components/ListItem';
import App from '../src/App';
// import {chai_app, mswServer} from './setup';
import mockApp from '../../server/server';


chai.use(chaiHttp);

// screen.logTestingPlaygroundURL();
describe('Auth', async () => {
    // before(() => {
    //     chai.request(mockApp); // Start the mock server before running tests
    //   });
    //   after(() => {
    //     mswServer.close(); // Close the mock server after running tests
    //   });

    it('should pass verification', async () => {
        // Use chai-http against the Express app server
        const res = await chai
        .request(mockApp)
        .post('http://localhost:8000/auth/login')
        .type('form')
        .send({
            email: '123@test.com',
            password: '123456',
        })
            expect(err).to.be.null;
            expect(res).to.have.status(200);
            done();
    })
})

describe('ListHeader', () => {
    it('should display create Modal when Add new button is clicked', () => {
        render(<ListHeader />);
        // screen.logTestingPlaygroundURL();
        // screen.debug()
        const addButton = screen.getByRole('button', {
            name: /add new/i
          })
        fireEvent.click(addButton);
        
        // Assert
        const createModal = screen.getByText('Lets create your task')
        expect(createModal).to.exist;

    })
})

describe('ListItem', () => {
    const task = {title: 'Sample'}
    const getData = () => {
        console.log('mocked getData')
        return title
    }

    beforeEach(() => {
        render(
            <ListItem task={task} getData={getData}/>
            )})
        it('should display edit Modal when edit button is clicked', () => {
        // Act
        const editButton = screen.getByTestId("edit");
        fireEvent.click(editButton);
        
        // Assert
        const editModal = screen.getByText('Lets edit your task')
        expect(editModal).to.exist;

        

    })
})

describe('App', async () => {
    it('should render the App component', async () => {
        render(<App/>)
        // screen.debug()
    })
})

//  chai http



